import Header from "./Header.js";
import Footer from "./Footer";

function QuestionsBut(props) {
    return (
        <main>
            <div>
                <Header />
            </div>
            <div className="questionAndAnswer"><p>{props.question}</p><p>{props.answer}</p></div>
            <div className="content-questions">
                <button className="question-button"><span>Pergunta 1</span> <ion-icon name="play-outline"></ion-icon></button>
                <button className="question-button"><span>Pergunta 2</span> <ion-icon name="play-outline"></ion-icon></button>
                <button className="question-button"><span>Pergunta 3</span> <ion-icon name="play-outline"></ion-icon></button>
                <button className="question-button"><span>Pergunta 4</span> <ion-icon name="play-outline"></ion-icon></button>
            </div>
            <div>
                <Footer />
            </div>
        </main>
    );
}


questionsData.sort(function randomize() {
    return Math.random() - 0.5;
});


function QuestionsData(object) {
    return (
        <div className="questionAndAnswer">
            <QuestionsBut question={object.question} answer={object.answer} />
        </div>
    );
}


const questionsData = [
    {
        question: "O que é JSX?",
        awnser: "Uma extensão de linguagem do JavaScript",
    },
    {
        question: "O React é __",
        awnser: "uma biblioteca JavaScript para construção de interfaces",
    },
    {
        question: "Componentes devem iniciar com ___",
        answer: "letra maiúscula",
    },
    {
        question: "Podemos colocar __ dentro do JSX ",
        answer: "expressões",
    },
    {
        question: "O ReactDOM nos ajuda __",
        answer: "interagindo com a DOM para colocar componentes React na mesma",
    },
    {
        question: "Usamos o npm para __ ",
        answer: "gerenciar os pacotes necessários e suas dependências",
    },
    {
        question: "Usamos props para __",
        answer: "passar diferentes informações para componentes",
    },
    {
        question: "Usamos estado (state) para __",
        answer: "dizer para o React quais informações quando atualizadas devem renderizar a tela novamente",
    },
];


export default function Questions() {
    return <div className="questionAndAnswer">{questionsData.map(QuestionsData)} </div>;
}
